import { StatusBar } from 'expo-status-bar';
import React, {useState} from 'react'; 
import { View, Image, ImageBackground, StyleSheet, Text, TextInput, Alert, ScrollView  } from 'react-native';
import { RectButton } from 'react-native-gesture-handler';
import { useNavigation } from '@react-navigation/native';
import SelectList from 'react-native-dropdown-select-list';

export default function AdicionarJogo() {

  const navigation = useNavigation();

  function handleNavigationToRegister() {
    navigation.navigate('Register');
  }

  const [selected, setSelected] = React.useState("");

  const data = [
    {key:'1',value:'Fortnite'},
    {key:'2',value:'League of Legends'},
    {key:'3',value:'Valorrant'},
  ];

  return (
    <View style={styles.container}>

      <ImageBackground source={require('../../assets/images/fundo-login.png')}
        style={styles.imgFundo}>
        <View style={styles.topoAdcJogos}>
          <Image style={styles.imgAdcJogos} source={require('../../assets/images/gwen.png')} />
          <Text style={styles.titulo}>Gwen</Text>
        </View>
        <View style={styles.bodyAdcJogos}>
        <View style={styles.SelecionarJogo}>
        <Text style={styles.txtTitulo}>Seleção de Jogos</Text>
        <View style={styles.selectJogo}>
            <Text style={styles.txtTitulo}>Selecione o Jogo:</Text>
            <SelectList setSelected={setSelected} data={data} />
        </View>
        </View>


          <View style={styles.menu}>
            <RectButton style={styles.btnMenu}>
              <Image style={styles.imgMenu} source={require('../../assets/images/home.png')} />
            </RectButton>
            <RectButton style={styles.btnMenu}>
              <Image style={styles.imgMenu} source={require('../../assets/images/chat.png')} />
            </RectButton>
            <RectButton style={styles.btnMenu}>
              <Image style={styles.imgMenu} source={require('../../assets/images/notificacao.png')} />
            </RectButton>
            <RectButton style={styles.btnMenu}>
              <Image style={styles.imgMenu} source={require('../../assets/images/perfil-ativo.png')} />
            </RectButton>
          </View>
        </View>


      </ImageBackground>

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },

  button: {
    width: 120,
    height: 45,
    backgroundColor: '#7EDD2A',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: 10,
    marginBottom: 8,
    marginTop: 8,
    paddingHorizontal: 24,
    fontSize: 16,
  },

  buttonText: {
    fontWeight: 'bold',
    fontSize: 17,
    color: '#FFF'
  },
  imgFundo: {
    flex: 1,
    width: '100%',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#3956FF',
    borderBottomLeftRadius: 50,
    borderBottomRightRadius: 50,
  },

  titulo: {
    color: '#FFF',
    fontSize: 25,
    fontWeight: 'bold'
  },

  topoAdcJogos: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-around',
    marginTop: 40
  },

  imgAdcJogos: {
    width: 150,
    height: 150,
    borderRadius: 110,
  },

  txtTitulo: {
    color: '#474747',
    fontSize: 20,
    fontWeight: 'bold',
    marginTop: 5
  },

  txtTituloAdc:{
    color: '#474747',
    fontSize: 16,
    fontWeight: 'bold',
    marginTop: 5
  },

  bodyAdcJogos: {
    backgroundColor: '#FFF',
    width: '100%',
    height: 480,
    borderTopEndRadius: 40,
    borderTopStartRadius: 40,
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-start'
  },


  buttonJogo: {
    width: 180,
    height: 40,
    backgroundColor: '#3956FF',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: 10,
    marginBottom: 2,
    marginTop: 4,
    paddingHorizontal: 24,
    fontSize: 16,
  },

  btnjogo: {
    display: 'flex',
    justifyContent: 'flex-end',
    flexDirection: 'row',
    width: '90%',
  },


  menu: {
    display: 'flex',
    justifyContent: 'space-around',
    height: 69,
    backgroundColor: '#FFF',
    width: '100%',
    flexDirection: 'row',
    alignItems: 'center',
    borderTopLeftRadius: 30,
    borderTopRightRadius: 30,
    shadowColor: "#000000",
    shadowOffset: {
      width: -20,
      height: -18,
    },
    shadowOpacity: 0.25,
    shadowRadius: 20.00,
    elevation: 24
  },

  btnMenu: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  },

  selectJogo:{
    height:200
  }

});
