import { StatusBar } from 'expo-status-bar';
import {Button, View, Image, ImageBackground, StyleSheet, Text, TextInput, Alert, ScrollView} from 'react-native'; 
import {RectButton} from 'react-native-gesture-handler'; 
import {useNavigation} from '@react-navigation/native'; 
import React, { useState } from 'react';
import DateTimePicker from '@react-native-community/datetimepicker';

export default function FormPartidas() {

  const navigation = useNavigation(); 


  const [datePicker, setDatePicker] = useState(false);

  const [date, setDate] = useState(new Date());

  const [timePicker, setTimePicker] = useState(false);

  const [time, setTime] = useState(new Date(Date.now()));

  function showDatePicker() {
    setDatePicker(true);
  };

  function showTimePicker() {
    setTimePicker(true);
  };

  function onDateSelected(event, value) {
    setDate(value);
    setDatePicker(false);
  };

  function onTimeSelected(event, value) {
    setTime(value);
    setTimePicker(false);
  };

  function handleNavigationToHome() {
    navigation.navigate('Home');
  }

  function handleNavigationToPerfil() {
    navigation.navigate('Perfil');
  }
  return (
    <View style={styles.container}>

      <ImageBackground source={require('../../assets/images/fundo-login.png')}
       style={styles.imgFundo}>
        <View style={styles.bodyPartGrupo}>

        <View style={styles.topoPartidas}>
          <Text style={styles.tituloDados}>Partidas</Text>
        </View>


        <Text style={styles.txtTitulo}>Partidas Marcadas:</Text>
        <View style={styles.rolagemPartidas}>
          <Text style={styles.tituloDados}>Data da Partida:</Text>

          <Text style={styles.text}>Date = {date.toDateString()}</Text>

          <Text style={styles.text}>Time = {time.toLocaleTimeString('en-US')}</Text>

          {datePicker && (
            <DateTimePicker
              value={date}
              mode={'date'}
              display={Platform.OS === 'ios' ? 'spinner' : 'default'}
              is24Hour={true}
              onChange={onDateSelected}
              style={styles.datePicker}
            />
          )}

          {timePicker && (
            <DateTimePicker
              value={time}
              mode={'time'}
              display={Platform.OS === 'ios' ? 'spinner' : 'default'}
              is24Hour={true}
              onChange={onTimeSelected}
              style={styles.datePicker}
            />
          )}

          {!datePicker && (
            <View style={{ margin: 10 }}>
              <Button title="Show Date Picker" color="green" onPress={showDatePicker} />
            </View>
          )}

          {!timePicker && (
            <View style={{ margin: 10 }}>
              <Button title="Show Time Picker" color="green" onPress={showTimePicker} />
            </View>
          )}


          <TextInput 
                style={styles.input} 
                placeholder="13/03/2022" 
                autoCapitalize="characters" 
                autoCorrect={false} 
            /> 
            <Text style={styles.tituloDados}>Hora da Partida:</Text>
          <TextInput 
                style={styles.input} 
                placeholder="19:30" 
                autoCapitalize="characters" 
                autoCorrect={false} 
            /> 
            <Text style={styles.tituloDados}>Descrição:</Text>
          <TextInput 
                style={styles.inputDesc} 
                multiline={true}
                autoCapitalize="characters" 
                autoCorrect={false} 
            /> 
        </View>
        <View style={styles.btnGrupo}>
        <RectButton style={styles.buttonGrupo} > 
            <Text style={styles.buttonText}>Marcar nova partida</Text> 
          </RectButton> 
        </View>

      <View style={styles.menu}>
        <RectButton style={styles.btnMenu}  onPress={handleNavigationToHome}> 
          <Image style={styles.imgMenu} source={require('../../assets/images/home.png')}/>
        </RectButton> 
        <RectButton style={styles.btnMenu}> 
          <Image style={styles.imgMenu} source={require('../../assets/images/chat-ativo.png')}/>
        </RectButton> 
        <RectButton style={styles.btnMenu}> 
          <Image style={styles.imgMenu} source={require('../../assets/images/notificacao.png')}/>
        </RectButton> 
        <RectButton style={styles.btnMenu} onPress={handleNavigationToPerfil}> 
          <Image style={styles.imgMenu} source={require('../../assets/images/perfil.png')}/>
        </RectButton> 
      </View>
        </View>


     </ImageBackground>

    </View>
  );
}

